// Parse Telegram message for business type, location, AND service offering
const message = $json.body.message.text.toLowerCase();
const chatId = $json.body.message.chat.id;
const userId = $json.body.message.from.id;
const userName = $json.body.message.from.first_name || 'User';

let businessType = '';
let city = '';
let countryCode = 'us';
let serviceOffering = '';
let serviceDescription = '';

// Enhanced pattern matching for service offerings
let cleanMessage = message;

// Extract service offering patterns
if (message.includes(' for ')) {
  const parts = message.split(' for ');
  cleanMessage = parts[0];
  serviceOffering = parts[1].trim();
} else if (message.includes(' - ')) {
  const parts = message.split(' - ');
  cleanMessage = parts[0];
  serviceOffering = parts[1].trim();
} else if (message.includes(' offering ')) {
  const parts = message.split(' offering ');
  cleanMessage = parts[0];
  serviceOffering = parts[1].trim();
} else if (message.includes(' | ')) {
  const parts = message.split(' | ');
  cleanMessage = parts[0];
  serviceOffering = parts[1].trim();
}

// Parse business type and location from cleaned message
if (cleanMessage.includes(' in ')) {
  const parts = cleanMessage.split(' in ');
  businessType = parts[0].trim();
  city = parts[1].trim();
} else if (cleanMessage.includes(' near ')) {
  const parts = cleanMessage.split(' near ');
  businessType = parts[0].trim();
  city = parts[1].trim();
} else {
  const words = cleanMessage.split(' ');
  if (words.length >= 3) {
    businessType = words.slice(0, -2).join(' ');
    city = words.slice(-2).join(' ');
  } else {
    businessType = words[0] || 'restaurant';
    city = words[1] || 'San Francisco';
  }
}

// Clean up formatting
businessType = businessType.replace(/[^a-zA-Z0-9\s]/g, '').trim();
city = city.replace(/[^a-zA-Z0-9\s,]/g, '').trim();
serviceOffering = serviceOffering.replace(/[^a-zA-Z0-9\s]/g, '').trim();

// Determine country code
if (city.toLowerCase().includes('ca') || city.toLowerCase().includes('california') || 
    city.toLowerCase().includes('ny') || city.toLowerCase().includes('texas') ||
    city.toLowerCase().includes('florida') || city.toLowerCase().includes('usa')) {
  countryCode = 'us';
} else if (city.toLowerCase().includes('london') || city.toLowerCase().includes('uk') || 
           city.toLowerCase().includes('england')) {
  countryCode = 'gb';
} else if (city.toLowerCase().includes('paris') || city.toLowerCase().includes('france')) {
  countryCode = 'fr';
} else if (city.toLowerCase().includes('toronto') || city.toLowerCase().includes('canada')) {
  countryCode = 'ca';
}

// Generate service descriptions based on common offerings
const serviceMap = {
  'digital marketing': 'comprehensive digital marketing services including SEO, social media management, and online advertising to grow your business',
  'seo': 'search engine optimization services to improve Google rankings and drive more organic traffic to your website',
  'voice ai': 'AI-powered voice automation solutions for customer service, appointment booking, and phone call management',
  'website development': 'professional website design and development services to create a modern, conversion-focused online presence',
  'social media': 'social media management and content creation services to build your brand and engage customers online',
  'lead generation': 'targeted lead generation and customer acquisition solutions to grow your client base consistently',
  'automation': 'business process automation solutions to streamline operations, reduce costs, and improve efficiency',
  'chatbot': 'AI chatbot development for 24/7 customer support, lead qualification, and customer engagement',
  'ppc': 'pay-per-click advertising management for Google Ads, Facebook Ads, and other platforms to drive immediate results',
  'content marketing': 'strategic content creation and marketing services to attract and convert your ideal customers',
  'email marketing': 'automated email marketing campaigns and newsletter management to nurture leads and retain customers',
  'crm': 'customer relationship management system setup and optimization to better manage your sales pipeline',
  'web design': 'modern, responsive website design and user experience optimization to convert more visitors into customers',
  'branding': 'brand identity development and marketing collateral design to establish a strong market presence'
};

// Match service offering to description
for (const [key, description] of Object.entries(serviceMap)) {
  if (serviceOffering.includes(key)) {
    serviceDescription = description;
    break;
  }
}

// Default service if none specified
if (!serviceOffering) {
  serviceOffering = 'digital marketing';
  serviceDescription = 'digital marketing services to help grow your business online and attract more customers';
}

return {
  json: {
    chatId: chatId,
    userId: userId,
    userName: userName,
    originalMessage: message,
    businessType: businessType,
    city: city,
    countryCode: countryCode,
    serviceOffering: serviceOffering,
    serviceDescription: serviceDescription,
    searchQuery: `${businessType} ${city}`,
    searchContext: `Looking for ${businessType} in ${city} to offer ${serviceOffering} services`,
    timestamp: new Date().toISOString()
  }
};

// Enhanced processing with review analysis for warm email intelligence
const results = $json;
const processedData = [];

// Handle both single result and array of results
const businesses = Array.isArray(results) ? results : [results];

for (const business of businesses) {
  if (!business || !business.title) continue;

  // Extract customer review insights for pain point analysis
  const reviewAnalysis = {
    customerComplaints: [],
    serviceIssues: [],
    busySignals: [],
    positiveAspects: [],
    commonThemes: []
  };

  if (business.reviews && Array.isArray(business.reviews)) {
    business.reviews.forEach(review => {
      const reviewText = (review.text || '').toLowerCase();

      // Look for service/operational issues
      if (reviewText.includes('busy') || reviewText.includes('hard to reach') || 
          reviewText.includes('no answer') || reviewText.includes('phone')) {
        reviewAnalysis.busySignals.push(review.text);
      }

      if (reviewText.includes('customer service') || reviewText.includes('staff') || 
          reviewText.includes('rude') || reviewText.includes('unhelpful')) {
        reviewAnalysis.serviceIssues.push(review.text);
      }

      if (reviewText.includes('wait') || reviewText.includes('slow') || 
          reviewText.includes('delay') || reviewText.includes('appointment')) {
        reviewAnalysis.customerComplaints.push(review.text);
      }

      if (review.stars >= 4) {
        reviewAnalysis.positiveAspects.push(review.text);
      }
    });
  }

  // Clean and extract enhanced data
  const processed = {
    businessName: business.title || '',
    address: business.address || business.location?.address || '',
    phone: business.phoneNumber || business.phone || '',
    website: business.website || business.url || '',
    rating: business.totalScore || business.rating || '',
    reviewCount: business.reviewsCount || business.reviews?.length || '',
    category: business.categoryName || business.category || business.categories?.[0] || '',
    googleMapsUrl: business.url || business.link || '',
    priceLevel: business.priceLevel || '',
    isOpen: business.isOpen || business.temporarilyClosedUntil || 'Unknown',

    // Enhanced review intelligence
    recentReviews: business.reviews?.slice(0, 5) || [],
    reviewPainPoints: {
      busyPhoneIssues: reviewAnalysis.busySignals.length,
      customerServiceIssues: reviewAnalysis.serviceIssues.length,
      operationalComplaints: reviewAnalysis.customerComplaints.length,
      positiveCount: reviewAnalysis.positiveAspects.length
    },

    // Business intelligence indicators
    businessIntelligence: {
      phoneContactDifficulty: reviewAnalysis.busySignals.length > 0,
      customerServiceGaps: reviewAnalysis.serviceIssues.length > 0,
      operationalStrain: business.reviewsCount > 50 && business.totalScore < 4.0,
      highDemand: business.reviewsCount > 100 && business.totalScore >= 4.0,
      needsDigitalSolution: !business.website || business.website === '',
      socialMediaGaps: true // Will be analyzed by AI
    },

    // Coordinates if available
    latitude: business.location?.lat || business.latitude || '',
    longitude: business.location?.lng || business.longitude || '',

    // Additional details
    description: business.description || '',
    imageUrl: business.imageUrl || business.photos?.[0] || '',

    // Fields for social media enrichment (to be filled by AI)
    linkedinUrl: '',
    twitterHandle: '',
    facebookUrl: '',
    instagramHandle: '',

    // AI-generated fields (to be filled by AI agent)
    warmIntroTopic: '',
    businessInsights: '',
    identifiedPainPoints: '',
    solutionMapping: '',
    relevanceScore: '',
    consultativeMessage: '',
    nextStepRecommendation: '',

    // Metadata
    scrapedAt: new Date().toISOString(),
    enrichmentStatus: 'pending',
    contactStatus: 'Not Contacted',

    // Service context from original search
    serviceOffering: $('Parse Business Search & Service Request').first().json.serviceOffering,
    serviceDescription: $('Parse Business Search & Service Request').first().json.serviceDescription,
    searchCity: $('Parse Business Search & Service Request').first().json.city,
    searchBusinessType: $('Parse Business Search & Service Request').first().json.businessType
  };

  // Only add businesses with valid names and some contact info
  if (processed.businessName && (processed.phone || processed.website || processed.address)) {
    processedData.push(processed);
  }
}

// Log summary
console.log(`Processed ${processedData.length} businesses with review intelligence`);

return processedData.map(item => ({json: item}));

// Enhanced social media profile detection and URL generation
const business = $json;
let enrichedBusiness = {...business};

// Generate likely social media profiles based on business name and website
if (business.businessName) {
  const cleanName = business.businessName
    .toLowerCase()
    .replace(/[^a-z0-9\s]/g, '')
    .replace(/\s+/g, '');

  const cleanNameWithDashes = business.businessName
    .toLowerCase()
    .replace(/[^a-z0-9\s]/g, '')
    .replace(/\s+/g, '-');

  // Generate likely social media URLs
  enrichedBusiness.linkedinUrl = `https://www.linkedin.com/company/${cleanNameWithDashes}`;
  enrichedBusiness.twitterHandle = `@${cleanName}`;
  enrichedBusiness.facebookUrl = `https://www.facebook.com/${cleanName}`;
  enrichedBusiness.instagramHandle = `@${cleanName}`;

  // If website exists, try to extract domain for better social media guessing
  if (business.website) {
    try {
      const domain = business.website
        .replace(/https?:\/\//g, '')
        .split('/')[0]
        .replace(/www\./g, '')
        .split('.')[0];

      enrichedBusiness.linkedinUrl = `https://www.linkedin.com/company/${domain}`;
      enrichedBusiness.twitterHandle = `@${domain}`;
      enrichedBusiness.facebookUrl = `https://www.facebook.com/${domain}`;
      enrichedBusiness.instagramHandle = `@${domain}`;
    } catch (error) {
      console.log('Could not parse website domain for', business.businessName);
    }
  }
}

// Mark for AI enrichment
enrichedBusiness.needsAiEnrichment = true;
enrichedBusiness.socialMediaGenerated = true;

return {json: enrichedBusiness};

